import './App.css';
import React, { Component } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


import { faSave } from '@fortawesome/free-solid-svg-icons';



//import Contactinfo from './components/Contactinfo';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullName: "FirstName LastName",
      editModeEnabled: false,
      email: "youremail@domain.com",
      editModeEnabledEmail: false,
      phoneNumber: {value: "1 (234) 567-8910", edit: false}

    };
    this.handleClick = this.handleClick.bind(this);
    this.handleClickNew = this.handleClickNew.bind(this);
    this.saveInput = this.saveInput.bind(this);

  }

  handleClick(e) {
    //alert('hello')
    console.log(e.target)
    //e.target.className = this.state.editModeEnabled
    this.setState(prevState => ({
      editModeEnabled: !prevState.editModeEnabled
    }));
  }

  handleClickNew(e) {
    //alert('hello')
    console.log(e.target.id)
    let temp = String(e.target.id);
    console.log(this.state.temp);

    //e.target.className = this.state.editModeEnabled
    this.setState(prevState => ({
      editModeEnabled: !prevState.editModeEnabled
    }));
  }

  saveInput(e) {
    e.preventDefault();
    console.log(e.target.childNodes[0].id) // this gets the id of the form to change
    let thingToChange = document.getElementById(e.target.childNodes[0].id).value; // need this to be configurable
    this.setState(prevState => ({
      editModeEnabled: !prevState.editModeEnabled
    }));
    this.setState(prevState => ({
      fullName: thingToChange
    }));
  }




  render() {
    console.log(this.state)
    let fullName;
    if (this.state.editModeEnabled)
    {
      fullName = <div>
        <form onSubmit={this.saveInput}>
        <input id="full-name" placeholder={this.state.fullName}></input>
      <button  type="submit"><FontAwesomeIcon icon={faSave} >
      </FontAwesomeIcon></button>
      </form>
      </div>

    }
    else
    {
      fullName =  <div><h2 id="full-name" onClick={this.handleClick}>{this.state.fullName}</h2></div>
    }
    let emailAddress;
    if (this.state.editModeEnabledEmail)
    {
      emailAddress = <div >
        <input id="email-address" placeholder={this.state.email}></input>
      <FontAwesomeIcon icon={faSave} onClick={this.saveInput}>
      </FontAwesomeIcon>
      </div>

    }
    else
    {
      emailAddress =  <div><h3 id="email-address" onClick={this.handleClick}>{this.state.email}</h3></div>
    }

    let phoneNumber;
    if (this.state.phoneNumber.edit)
    {
      phoneNumber = <div >
        <input id="phoneNumber" placeholder={this.state.phoneNumber.value}></input>
      <FontAwesomeIcon icon={faSave} onClick={this.saveInput}> 
      </FontAwesomeIcon>
      </div>

    }
    else
    {
      phoneNumber =  <div><h3 id="phoneNumber" onClick={this.handleClickNew}>{this.state.phoneNumber.value}</h3></div>
    }


    //console.log(fullName)
    return (
    <div className="App">
      {fullName}
      {emailAddress}
      {phoneNumber}
      <hr></hr>
    </div>
  );
  } 
}



export default App;
