// Job.js
// School.js

import React, {Component} from 'react'
import './component.css';
//import Editablefield from './Editablefield'


class Job extends Component{

    constructor(props) {
        super(props)

        this.state = {
            school: "",
            degree: "",
            startDate: "",
            endDate: "",
            text: props.value,
            isEditing: false,
            tag: props.tag,
            default: props.value
        }
    }

    handleClick = () => {
        this.setState({
            isEditing: !this.state.isEditing
        });
        console.log(this)
    }

    handleSchoolChange = (e) => {
        this.setState({school: e.target.value});
    }

    handleDegreeChange = (e) => {
        this.setState({degree: e.target.value});
    }
    handleStartDateChange = (e) => {
        this.setState({startDate: e.target.value});
    }
    handleEndDateChange = (e) => {
        this.setState({endDate: e.target.value});
    }




    render() {
        console.log(this.props)
        console.log(this.state)
    return (
        !this.state.isEditing ?
		<div className="school">
            <form>
            <label for="school">Company</label>
            <input type="text" onChange={this.handleSchoolChange} placeholder="School" value={this.state.school}></input> 
            <label for="degree">Role</label>
            <input type="text" onChange={this.handleDegreeChange}placeholder="Degree Attained" value={this.state.degree}></input><br></br>
            Start Date
            <input type="text" onChange={this.handleStartDateChange} placeholder="MM-YYYY" value={this.state.startDate}></input> 
            End Date
            <input type="text" onChange={this.handleEndDateChange} placeholder="MM-YYYY" value={this.state.endDate}></input><br></br>
            <input type="textarea" placeholder="Enter a few sentences description here"></input><br></br>
            <button type="button" onClick={this.handleClick}>Save</button>
            <br></br>
            </form>
        </div>
         : <div className="school" onClick={this.handleClick}>
         <p>{this.state.school}</p>
         <p>{this.state.degree}</p>
         <p>{this.state.startDate} - {this.state.endDate}</p>
         <p></p>
         <br></br>
         </div>
        )
    }
}

export default Job;
