// School.js

import React, {Component} from 'react'




class School extends Component{

    constructor(props) {
        super(props)

        this.state = {
            text: props.value,
            isEditing: false,
            tag: props.tag,
            default: props.value
        }
    }

    handleClick = () => {
        this.setState({
            text: this.state.text,
            isEditing: true
        })
    }


    render() {
    return (
		<div className="school">
            <form>
            <input type="text" value="School"></input> 
            <input type="text" value="Degree Attained"></input> 
            <input type="text" value="Start Date"></input> 
            <input type="text" value="End Date"></input> 
            <button type="button" onClick={this.handleClick}>Save</button>
            <br></br>
            </form>
            <button>X</button>
        </div>
        )
    }
}

export default School;
