import React, {Component} from 'react'
import School from './School';

import './component.css';


class Education extends Component {
    constructor(props) {
        super(props)
        this.state = {
            numEducations: 0,
            educationArr: []
        }
        this.addEducation = this.addEducation.bind(this);
    }

    addEducation = () => {
        this.setState({
            numEducations: this.state.numEducations + 1
        });
        console.log(this.state.numEducations);
    }

    removeEducation = (e) => {
        console.log(e.target.parentNode);
    }


    render() {
        const educations = [];
        for (let i = 0; i < this.state.numEducations; i += 1) {
            educations.push(<div><School></School><button onClick={this.removeEducation}>X</button></div>);

        }

        return (
            <div className="education">
                <h2 id="education-section-title">Education</h2>
                <hr></hr>
                {educations}
                <button onClick={this.addEducation}>Add Education</button>
            </div>
        )
    }
}

export default Education;
