import './App.css';
import React, { Component } from "react";
import Contactinfo from './components/Contactinfo';

class App extends Component {
  constructor() {
    super();

    this.state = {
      firstName: "FirstName",
      editModeEnabled: false,
      lastName: "LastName",
      fullName: "FirstName LastName",
      phoneNumber: "1 (234) 567 8910",
      emailAddress: "your@email.com",
    };

    //this.editValue = this.editValue.bind(this);



  }

  handleClick(e) {
    this.setState({
      editModeEnabled: e.target.disabled
    })
    console.log('yes')
    alert('worked?')
  }


  render() {
  return (
    <div className="App">
      <input id="full-name" type="text" value={this.props.fullName} disabled={!this.props.editModeEnabled} onClick={this.props.handleClick}></input>
      <Contactinfo firstName={this.state.firstName} lastName={this.state.lastName} disabled={!this.state.editModeEnabled}
      phoneNumber={this.state.phoneNumber} emailAddress={this.state.emailAddress} onClick={this.handleClick}
      ></Contactinfo>
    </div>
  );
  }
}

export default App;
