// School.js

import React, {Component} from 'react'
//import Editablefield from './Editablefield'


class School extends Component{

    constructor(props) {
        super(props)

        this.state = {
            school: "School",
            degree: "Degree",
            text: props.value,
            isEditing: false,
            tag: props.tag,
            default: props.value
        }
    }

    handleClick = () => {
        this.setState({
            school: "Nober Dang",
            degree: "Bachelors",
            isEditing: true
        })
        console.log(this)
    }


    render() {
        console.log(this.props)
    return (
        !this.state.isEditing ?
		<div className="school">
            <form>
            <input type="text" id={"school-input"+this.props.id} placeholder="School"></input> 
            <input type="text" placeholder="Degree Attained"></input> 
            <input type="text" placeholder="Start Date"></input> 
            <input type="text" placeholder="End Date"></input> 
            <button type="button" onClick={this.handleClick}>Save</button>
            <br></br>
            </form>
            <button>X</button>
        </div>
         : <div className="school">
         <p>{this.state.school}</p>
         <p>{this.state.degree}</p>
         <p> Start Date</p>
         <p> End Date</p>
         <br></br>
         <button>X</button>
         </div>
        )
    }
}

export default School;
