import './App.css';
import React, { Component } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


import { faSave } from '@fortawesome/free-solid-svg-icons';
import Personal from './components/Personal';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullName: "FirstName LastName",
      editModeEnabled: false,
      email: "youremail@domain.com",
      editModeEnabledEmail: false,
      phoneNumber: {value: "1 (234) 567-8910", edit: false}

    };
    this.handleClick = this.handleClick.bind(this);
    this.handleClickNew = this.handleClickNew.bind(this);
    this.saveInput = this.saveInput.bind(this);

  }

  handleClick(e) {
    //alert('hello')
    console.log(e.target)
    //e.target.className = this.state.editModeEnabled
    this.setState(prevState => ({
      editModeEnabled: !prevState.editModeEnabled
    }));
  }



  saveInput(e) {
    e.preventDefault();
    console.log(e.target.childNodes[0].id) // this gets the id of the form to change
    let thingToChange = document.getElementById(e.target.childNodes[0].id).value; // need this to be configurable
    this.setState(prevState => ({
      editModeEnabled: !prevState.editModeEnabled
    }));
    this.setState(prevState => ({
      fullName: thingToChange
    }));
  }




  render() {
    console.log(this.state)

    //console.log(fullName)
    return (
    <div className="App">
      <Personal ></Personal>
      <hr></hr>
    </div>
  );
  } 
}



export default App;
