import './App.css';
import React, { Component } from "react";
//import Contactinfo from './components/Contactinfo';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "FirstName",
      editModeEnabled: false,
      lastName: "LastName",
      fullName: "FirstName LastName",
      phoneNumber: "1 (234) 567 8910",
      emailAddress: "your@email.com",
    };

    this.handleClick = this.handleClick.bind(this);

  }

  handleClick() {
    this.setState(prevState => ({
      editModeEnabled: !prevState.editModeEnabled
    }));
  }


  render() {
  return (
    <div className="App">
      <input id="full-name" type="text" value={this.state.fullName} disabled={!this.state.editModeEnabled} onClick={this.handleClick}></input>
    </div>
  );
  }
}

export default App;
