import './App.css';
import React, { Component } from "react";
//import Contactinfo from './components/Contactinfo';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "FirstName",
      editModeEnabled: false,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    //alert('hello')
    console.log(e.target)
    e.target.className = 'input'
    this.setState(prevState => ({
      editModeEnabled: !prevState.editModeEnabled
    }));
  }



  render() {
    const editModeEnabled = this.state.editModeEnabled;
    let fullName;
    if (editModeEnabled) {
      fullName = <FullName />;
    }
    else {
      fullName = <FullNameEditable />;
    }

    return (
    <div className="App">
      {fullName}
    </div>
  );
  } 
}

function FullNameEditable(props) {
  return (
    <input onClick={this.state.handleClick} id="full-name">
      {this.state.fullName}
    </input>
  );
}

function FullName(props) {
  return (
    <h1 id="full-name" onClick={this.handleClick}>{this.state.fullName}</h1>
  );
}

export default App;
