// Contactinfo.js

import './Contactinfo.css';

import React, { Component } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


import { faSave } from '@fortawesome/free-solid-svg-icons';



class Contactinfo extends React.Component {

    flipEditMode(propToChange) {
        console.log('Flip edit mode');
        let editModeOn = !propToChange.edit;
        propToChange.edit = editModeOn;
        console.log(editModeOn);
        console.log(this.props)
    }


    render () {
        console.log(this.props)
        let phoneNumber;
        if (this.state.phoneNumber.edit)
        {
        phoneNumber = <div >
            <input id="phoneNumber" placeholder={this.state.phoneNumber.value}></input>
            <FontAwesomeIcon icon={faSave} onClick={this.saveInput}> 
            </FontAwesomeIcon>
            </div>
        }
        else
        {
            phoneNumber =  <div><h3 id="phoneNumber" onClick={this.handleClickNew}>{this.state.phoneNumber.value}</h3></div>
        }
    return (
        <div id="contact-info">
        <h3 onClick={() => {this.flipEditMode(this.props.phoneNumber)}}> {this.props.phoneNumber.value} </h3>
        </div>
        );

    }
}




export default Contactinfo;
