import './App.css';
import React, { Component } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


import { faSave } from '@fortawesome/free-solid-svg-icons';



//import Contactinfo from './components/Contactinfo';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullName: "FirstName LastName",
      editModeEnabled: false,
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleInput= this.handleInput.bind(this);

  }

  handleClick(e) {
    //alert('hello')
    console.log(e)
    //e.target.className = this.state.editModeEnabled
    this.setState(prevState => ({
      editModeEnabled: !prevState.editModeEnabled
    }));
  }

  handleInput(e) {
    let fullName = document.getElementById('full-name').value;
    this.setState(prevState => ({
      editModeEnabled: !prevState.editModeEnabled
    }));
    this.setState(prevState => ({
      fullName: fullName
    }));
  }




  render() {
    console.log(this.state)
    let fullName;
    if (this.state.editModeEnabled)
    {
      fullName = <div >
        <input id="full-name" placeholder={this.state.fullName} value={this.state.fullName}></input>
      <FontAwesomeIcon icon={faSave} onClick={this.handleInput}>
      </FontAwesomeIcon>
      </div>

    }
    else
    {
      fullName =  <h2 id="full-name" onClick={this.handleClick}>{this.state.fullName}</h2>
    }
    console.log(fullName)
    return (
    <div className="App">
      {fullName}
      <h3>{"youremail@domain.com"}</h3>
      <h3>{"1 (234) 567-8910"}</h3>
      <hr></hr>
    </div>
  );
  } 
}



export default App;
