// School.js

import React, {Component} from 'react'




class School extends Component{

    constructor(props) {
        super(props)

        this.state = {
            school: "School",
            degree: "Degree",
            text: props.value,
            isEditing: false,
            tag: props.tag,
            default: props.value
        }
    }

    handleClick = () => {
        this.setState({
            text: this.state.text,
            isEditing: true
        })
    }


    render() {
    return (
        !this.state.isEditing ?
		<div className="school">
            <form>
            <input type="text" placeholder="School"></input> 
            <input type="text" placeholder="Degree Attained"></input> 
            <input type="text" placeholder="Start Date"></input> 
            <input type="text" placeholder="End Date"></input> 
            <button type="button" onClick={this.handleClick}>Save</button>
            <br></br>
            </form>
            <button>X</button>
        </div>
         : <div className="school">
         <form>
         <p>{this.state.school}</p>
         <p>{this.state.degree}</p>
         <p> Start Date</p>
         <p> End Date</p>
         <br></br>
         </form>
         <button>X</button>
         </div>
        )
    }
}

export default School;
