// School.js

import React, {Component} from 'react'
//import Editablefield from './Editablefield'


class School extends Component{

    constructor(props) {
        super(props)

        this.state = {
            school: "School",
            degree: "Degree",
            startDate: "MM-YYYY",
            text: props.value,
            isEditing: false,
            tag: props.tag,
            default: props.value
        }
    }

    handleClick = () => {
        this.setState({
            isEditing: true
        })
        console.log(this)
    }

    handleSchoolChange = (e) => {
        this.setState({school: e.target.value});
    }

    handleDegreeChange = (e) => {
        this.setState({degree: e.target.value});
    }
    handleStartDateChange = (e) => {
        this.setState({startDate: e.target.value});
    }


    render() {
        console.log(this.props)
    return (
        !this.state.isEditing ?
		<div className="school">
            <form>
            <input type="text" onChange={this.handleSchoolChange} placeholder="School" value={this.state.school}></input> 
            <input type="text" onChange={this.handleDegreeChange}placeholder="Degree Attained"></input> 
            Start Date
            <input type="date" onChange={this.handleStartDateChange} placeholder="Start Date" value={this.state.school}></input> 
            End Date
            <input type="date" placeholder="End Date"></input> 
            <input typ="" placeholder="Enter a few sentences description here"></input>
            <button type="button" onClick={this.handleClick}>Save</button>
            <br></br>
            </form>
        </div>
         : <div className="school">
         <p>{this.state.school}</p>
         <p>{this.state.degree}</p>
         <p>{this.state.startDate} - End Date</p>
         <p></p>
         <br></br>
         </div>
        )
    }
}

export default School;
