import './App.css';
import React, { Component } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


import { faSave } from '@fortawesome/free-solid-svg-icons';



//import Contactinfo from './components/Contactinfo';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullName: "FirstName LastName",
      editModeEnabled: false,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    //alert('hello')
    console.log(e.target)
    //e.target.className = this.state.editModeEnabled
    this.setState(prevState => ({
      editModeEnabled: !prevState.editModeEnabled
    }));
  }


  render() {
    console.log(this.state)
    let fullName;
    if (this.state.editModeEnabled)
    {
      fullName = <div ><input id="full-name" value={this.state.fullName}></input>
      <FontAwesomeIcon icon={faSave} onClick={this.handleClick}>
      </FontAwesomeIcon>
      </div>

    }
    else
    {
      fullName =  <h1 id="full-name" onClick={this.handleClick}>{this.state.fullName}</h1>
    }
    console.log(fullName)
    return (
    <div className="App">
      {fullName}
    </div>
  );
  } 
}



export default App;
